// Get order ready for book assembly
const orderData = $input.first().json;

// Check if order is ready for book assembly
if (orderData.status !== 'ready_for_book_assembly') {
  return []; // Skip if not ready
}

// Update order status to assembling
const assemblingOrder = {
  ...orderData,
  status: 'book_assembly_in_progress',
  assemblyStartedAt: new Date().toISOString(),
  pagesGenerated: 0,
  totalPagesRequired: 14, // 14 pages for production
  assemblyProgress: 0
};

console.log('Starting book assembly for order: ' + orderData.amazonOrderId);
return [{ json: assemblingOrder }];

// Load all generated character images for the order
const orderData = $input.first().json;

// Use public R2 URL like Workflow 2
const publicR2Url = 'https://pub-92cec53654f84771956bc84dfea65baa.r2.dev';
const characterHash = orderData.characterHash;

// Validate required fields
if (!characterHash) {
  throw new Error('Character hash is required for loading character images');
}

// Validate character hash format (should be 16 characters)
if (!/^[a-f0-9]{16}$/.test(characterHash)) {
  throw new Error('Invalid character hash format: ' + characterHash + '. Expected 16-character hex string.');
}

// Load base character and all poses
const characterImages = {
  base: publicR2Url + '/book-mvp-simple-adventure/order-generated-assets/characters/' + characterHash + '/base-character.png',
  poses: []
};

// Load all pose images (1-14) with validation
for (let i = 1; i <= 14; i++) {
  const poseFilename = getPoseFilename(i);
  if (poseFilename) {
    const imagePath = publicR2Url + '/book-mvp-simple-adventure/order-generated-assets/characters/' + characterHash + '/characters_' + characterHash + '_pose' + i.toString().padStart(2, '0') + '.png';

    characterImages.poses.push({
      poseNumber: i,
      poseFilename: poseFilename,
      imagePath: imagePath,
      pageNumber: i,
      validated: false // Will be validated when accessed
    });
  }
}

// Validate that we have the expected number of poses
const expectedPoses = 12; // Pages 1-12 have child poses, 13-14 have special cases
const actualPoses = characterImages.poses.length;
if (actualPoses !== expectedPoses) {
  console.warn('Expected ' + expectedPoses + ' poses, found ' + actualPoses + '. This may cause issues.');
}

function getPoseFilename(pageNumber) {
  const poseMap = {
    1: 'walking',
    2: 'walking-looking-higher',
    3: 'looking',
    4: 'floating',
    5: 'walking-looking-down',
    6: 'jogging',
    7: 'looking',
    8: 'sitting-eating',
    9: 'crouching',
    10: 'crawling-moving-happy',
    11: 'surprised-looking-up',
    12: 'surprised',
    13: null, // Tiger only, no child character
    14: 'flying'
  };
  return poseMap[pageNumber];
}

const orderWithCharacters = {
  ...orderData,
  characterImages: characterImages,
  publicR2Url: publicR2Url
};

console.log('Loaded ' + characterImages.poses.length + ' character poses for assembly from ' + publicR2Url);
return [{ json: orderWithCharacters }];

// Load all background images for the Voice of Wonder story
const orderData = $input.first().json;

// Use public R2 URL like Workflow 2
const publicR2Url = orderData.publicR2Url || 'https://pub-92cec53654f84771956bc84dfea65baa.r2.dev';

// Validate required fields
if (!orderData.amazonOrderId) {
  throw new Error('Amazon Order ID is required for loading background images');
}

// Load background images for all 14 pages with validation
const backgroundImages = [];
for (let i = 1; i <= 14; i++) {
  const sceneName = getSceneName(i);
  const imagePath = publicR2Url + '/book-mvp-simple-adventure/backgrounds/page' + i.toString().padStart(2, '0') + '-' + getSceneSlug(i) + '.png';

  backgroundImages.push({
    pageNumber: i,
    imagePath: imagePath,
    sceneName: sceneName,
    validated: false // Will be validated when accessed
  });
}

// Validate that we have the expected number of backgrounds
const expectedBackgrounds = 14;
const actualBackgrounds = backgroundImages.length;
if (actualBackgrounds !== expectedBackgrounds) {
  throw new Error('Expected ' + expectedBackgrounds + ' background images, found ' + actualBackgrounds);
}

function getSceneSlug(pageNumber) {
  const sceneSlugs = [
    'twilight-walk', 'night-forest', 'magic-doorway', 'courage-leap', 'morning-meadow',
    'tall-forest', 'mountain-vista', 'picnic-surprise', 'beach-discovery', 'crystal-cave',
    'giant-flowers', 'almost-there', 'animal-reveal', 'flying-home'
  ];
  return sceneSlugs[pageNumber - 1] || 'unknown';
}

function getSceneName(pageNumber) {
  const scenes = [
    'twilight_walk', 'night_forest', 'magic_doorway', 'courage_leap', 'morning_meadow',
    'tall_forest', 'mountain_vista', 'picnic_surprise', 'beach_discovery', 'crystal_cave',
    'giant_flowers', 'enchanted_grove', 'animal_reveal', 'flying_home'
  ];
  return scenes[pageNumber - 1] || 'unknown';
}

const orderWithBackgrounds = {
