  });
}

console.log('Initialized page generation for ' + pagesToGenerate.length + ' pages');
return pagesToGenerate.map(page => ({ json: page }));

// Generate complete multi-page HTML document for PDFMonkey
const orderData = $input.first().json;

// Debug: Log what data we have
console.log('Order data keys:', Object.keys(orderData));
