// Check overall system health
const healthChecks = {
  timestamp: new Date().toISOString(),
  systemStatus: 'operational',
  checks: {}
};

// Check n8n instance health
healthChecks.checks.n8n = {
  status: 'healthy',
  responseTime: Math.random() * 100 + 50, // Simulated response time
  lastExecution: new Date(Date.now() - Math.random() * 300000).toISOString(), // Within last 5 minutes
  errorRate: Math.random() * 0.05 // 0-5% error rate
};

// Check database connectivity
healthChecks.checks.database = {
  status: 'healthy',
  responseTime: Math.random() * 50 + 10,
  connectionPool: Math.floor(Math.random() * 20) + 5,
  activeQueries: Math.floor(Math.random() * 10)
};

// Check external API health
healthChecks.checks.openai = {
  status: Math.random() > 0.1 ? 'healthy' : 'degraded',
  responseTime: Math.random() * 2000 + 500,
  rateLimitRemaining: Math.floor(Math.random() * 1000) + 100,
  lastError: Math.random() > 0.8 ? 'Rate limit exceeded' : null
};

healthChecks.checks.lulu = {
  status: Math.random() > 0.05 ? 'healthy' : 'degraded',
  responseTime: Math.random() * 1500 + 300,
  lastError: Math.random() > 0.9 ? 'Authentication failed' : null
};

healthChecks.checks.cloudflareR2 = {
  status: 'healthy',
  responseTime: Math.random() * 100 + 50,
  storageUsed: Math.random() * 1000000000 + 500000000, // 500MB - 1.5GB
  requestsPerMinute: Math.floor(Math.random() * 100) + 10
};

// Calculate overall system status
const unhealthyServices = Object.values(healthChecks.checks).filter(check => check.status !== 'healthy');
if (unhealthyServices.length > 0) {
  healthChecks.systemStatus = unhealthyServices.length === 1 ? 'degraded' : 'critical';
}

console.log(`System health check: ${healthChecks.systemStatus}`);
return [{ json: healthChecks }];

// Monitor API costs and usage
const costMonitoring = {
  timestamp: new Date().toISOString(),
  period: 'last_24_hours',
  totalCost: 0,
  services: {}
};

// OpenAI costs (simulated)
const openaiCosts = {
  service: 'OpenAI GPT-4 Vision',
  requests: Math.floor(Math.random() * 100) + 50,
  costPerRequest: 0.01,
  totalCost: 0
};
openaiCosts.totalCost = openaiCosts.requests * openaiCosts.costPerRequest;
costMonitoring.services.openai = openaiCosts;
costMonitoring.totalCost += openaiCosts.totalCost;

// Remove.bg costs (simulated)
const removeBgCosts = {
  service: 'Remove.bg API',
  requests: Math.floor(Math.random() * 200) + 100,
  costPerRequest: 0.02,
  totalCost: 0
};
removeBgCosts.totalCost = removeBgCosts.requests * removeBgCosts.costPerRequest;
costMonitoring.services.removebg = removeBgCosts;
costMonitoring.totalCost += removeBgCosts.totalCost;

// Lulu costs (simulated)
const luluCosts = {
  service: 'Lulu Print-on-Demand',
  orders: Math.floor(Math.random() * 20) + 10,
  costPerOrder: 5.50,
  totalCost: 0
};
luluCosts.totalCost = luluCosts.orders * luluCosts.costPerOrder;
costMonitoring.services.lulu = luluCosts;
costMonitoring.totalCost += luluCosts.totalCost;

// Cloudflare R2 costs (simulated)
const r2Costs = {
  service: 'Cloudflare R2 Storage',
  storageGB: Math.random() * 10 + 5,
  requests: Math.floor(Math.random() * 1000) + 500,
  costPerGB: 0.015,
  costPerRequest: 0.0004,
  totalCost: 0
};
r2Costs.totalCost = (r2Costs.storageGB * r2Costs.costPerGB) + (r2Costs.requests * r2Costs.costPerRequest);
costMonitoring.services.cloudflareR2 = r2Costs;
costMonitoring.totalCost += r2Costs.totalCost;

// Check for cost anomalies
const dailyBudget = 50; // $50 daily budget
costMonitoring.budgetExceeded = costMonitoring.totalCost > dailyBudget;
costMonitoring.budgetUtilization = (costMonitoring.totalCost / dailyBudget) * 100;

console.log(`API costs: $${costMonitoring.totalCost.toFixed(2)} (${costMonitoring.budgetUtilization.toFixed(1)}% of budget)`);
return [{ json: costMonitoring }];

// Check processing queue status
const queueStatus = {
  timestamp: new Date().toISOString(),
  queues: {}
};

// Order intake queue
queueStatus.queues.orderIntake = {
  name: 'Order Intake',
  pending: Math.floor(Math.random() * 5),
  processing: Math.floor(Math.random() * 3) + 1,
  completed: Math.floor(Math.random() * 20) + 10,
  failed: Math.floor(Math.random() * 2),
  averageProcessingTime: Math.floor(Math.random() * 300) + 60 // 1-6 minutes
};

// AI generation queue
queueStatus.queues.aiGeneration = {
  name: 'AI Character Generation',
  pending: Math.floor(Math.random() * 3),
  processing: Math.floor(Math.random() * 2) + 1,
  completed: Math.floor(Math.random() * 15) + 5,
  failed: Math.floor(Math.random() * 1),
  averageProcessingTime: Math.floor(Math.random() * 1800) + 600 // 10-40 minutes
};

// Book assembly queue
queueStatus.queues.bookAssembly = {
  name: 'Book Assembly',
  pending: Math.floor(Math.random() * 2),
  processing: Math.floor(Math.random() * 2),
  completed: Math.floor(Math.random() * 12) + 3,
  failed: Math.floor(Math.random() * 1),
  averageProcessingTime: Math.floor(Math.random() * 600) + 300 // 5-15 minutes
};

// Print fulfillment queue
queueStatus.queues.printFulfillment = {
  name: 'Print & Fulfillment',
  pending: Math.floor(Math.random() * 3),
  processing: Math.floor(Math.random() * 2),
  completed: Math.floor(Math.random() * 8) + 2,
  failed: Math.floor(Math.random() * 1),
  averageProcessingTime: Math.floor(Math.random() * 1200) + 300 // 5-25 minutes
};

// Calculate overall queue health
const totalPending = Object.values(queueStatus.queues).reduce((sum, queue) => sum + queue.pending, 0);
const totalFailed = Object.values(queueStatus.queues).reduce((sum, queue) => sum + queue.failed, 0);
const totalProcessing = Object.values(queueStatus.queues).reduce((sum, queue) => sum + queue.processing, 0);

queueStatus.overall = {
  totalPending: totalPending,
  totalProcessing: totalProcessing,
  totalFailed: totalFailed,
  health: totalFailed > 3 ? 'critical' : totalPending > 10 ? 'degraded' : 'healthy'
};

console.log(`Queue status: ${queueStatus.overall.health} (${totalPending} pending, ${totalFailed} failed)`);
return [{ json: queueStatus }];

// Validate recently generated images
const imageValidation = {
  timestamp: new Date().toISOString(),
  period: 'last_hour',
  totalImages: 0,
  validImages: 0,
  invalidImages: 0,
  qualityIssues: []
};

// Simulate image validation results
const totalImages = Math.floor(Math.random() * 50) + 20;
const invalidImages = Math.floor(Math.random() * 3);
const validImages = totalImages - invalidImages;

imageValidation.totalImages = totalImages;
imageValidation.validImages = validImages;
imageValidation.invalidImages = invalidImages;
imageValidation.successRate = (validImages / totalImages) * 100;

// Simulate quality issues
const qualityIssues = [
  'Low resolution',
  'Missing transparency',
  'Incorrect dimensions',
  'Poor character consistency',
  'Background removal failure'
];

for (let i = 0; i < invalidImages; i++) {
  imageValidation.qualityIssues.push({
    imageId: `img_${Math.random().toString(36).substr(2, 9)}`,
    issue: qualityIssues[Math.floor(Math.random() * qualityIssues.length)],
    severity: Math.random() > 0.5 ? 'high' : 'medium',
    detectedAt: new Date(Date.now() - Math.random() * 3600000).toISOString()
  });
}

// Check for quality thresholds
imageValidation.qualityAlert = imageValidation.successRate < 90;
imageValidation.criticalIssues = imageValidation.qualityIssues.filter(issue => issue.severity === 'high').length;

console.log(`Image validation: ${imageValidation.successRate.toFixed(1)}% success rate`);
return [{ json: imageValidation }];

// Check for alert conditions across all monitoring data
const systemHealth = $('Check System Health').first().json;
const costMonitoring = $('Monitor API Costs').first().json;
const queueStatus = $('Check Queue Status').first().json;
const imageValidation = $('Validate Generated Images').first().json;

const alerts = [];
const alertLevel = 'info'; // info, warning, critical

// System health alerts
if (systemHealth.systemStatus === 'critical') {
  alerts.push({
    type: 'system_health',
    level: 'critical',
    message: 'System is in critical state',
    details: systemHealth,
    timestamp: new Date().toISOString()
  });
} else if (systemHealth.systemStatus === 'degraded') {
  alerts.push({
    type: 'system_health',
    level: 'warning',
    message: 'System performance is degraded',
    details: systemHealth,
    timestamp: new Date().toISOString()
  });
}

// Cost alerts
if (costMonitoring.budgetExceeded) {
  alerts.push({
    type: 'cost_alert',
    level: 'critical',
    message: `Daily budget exceeded: $${costMonitoring.totalCost.toFixed(2)}`,
    details: costMonitoring,
    timestamp: new Date().toISOString()
  });
} else if (costMonitoring.budgetUtilization > 80) {
  alerts.push({
    type: 'cost_alert',
    level: 'warning',
    message: `High budget utilization: ${costMonitoring.budgetUtilization.toFixed(1)}%`,
    details: costMonitoring,
    timestamp: new Date().toISOString()
  });
}

// Queue alerts
if (queueStatus.overall.health === 'critical') {
  alerts.push({
    type: 'queue_alert',
    level: 'critical',
    message: `Queue health is critical: ${queueStatus.overall.totalFailed} failed jobs`,
    details: queueStatus,
    timestamp: new Date().toISOString()
  });
} else if (queueStatus.overall.totalPending > 15) {
  alerts.push({
    type: 'queue_alert',
    level: 'warning',
    message: `High queue backlog: ${queueStatus.overall.totalPending} pending jobs`,
    details: queueStatus,
    timestamp: new Date().toISOString()
  });
}

// Image quality alerts
if (imageValidation.qualityAlert) {
  alerts.push({
    type: 'quality_alert',
    level: 'warning',
    message: `Image quality below threshold: ${imageValidation.successRate.toFixed(1)}% success rate`,
    details: imageValidation,
    timestamp: new Date().toISOString()
  });
}

if (imageValidation.criticalIssues > 0) {
  alerts.push({
    type: 'quality_alert',
    level: 'critical',
    message: `${imageValidation.criticalIssues} critical image quality issues detected`,
    details: imageValidation,
    timestamp: new Date().toISOString()
  });
}

const alertSummary = {
  timestamp: new Date().toISOString(),
  totalAlerts: alerts.length,
  criticalAlerts: alerts.filter(alert => alert.level === 'critical').length,
  warningAlerts: alerts.filter(alert => alert.level === 'warning').length,
  alerts: alerts
};

console.log(`Alert check: ${alertSummary.totalAlerts} alerts (${alertSummary.criticalAlerts} critical, ${alertSummary.warningAlerts} warnings)`);
return [{ json: alertSummary }];

// Send status reports to monitoring dashboard
const alertSummary = $input.first().json;
const systemHealth = $('Check System Health').first().json;
const costMonitoring = $('Monitor API Costs').first().json;
const queueStatus = $('Check Queue Status').first().json;
const imageValidation = $('Validate Generated Images').first().json;

const statusReport = {
  timestamp: new Date().toISOString(),
  systemHealth: systemHealth,
  costMonitoring: costMonitoring,
  queueStatus: queueStatus,
  imageValidation: imageValidation,
  alerts: alertSummary,
  overallStatus: getOverallStatus(systemHealth, costMonitoring, queueStatus, imageValidation)
};

function getOverallStatus(health, costs, queues, images) {
  if (health.systemStatus === 'critical' || costs.budgetExceeded || queues.overall.health === 'critical') {
    return 'critical';
  }
  if (health.systemStatus === 'degraded' || costs.budgetUtilization > 80 || queues.overall.totalPending > 15 || images.qualityAlert) {
    return 'warning';
  }
  return 'healthy';
}

console.log(`Status report generated: ${statusReport.overallStatus}`);
return [{ json: statusReport }];

