// Log monitoring results for analysis
const statusReport = $input.first().json;

const logEntry = {
  timestamp: new Date().toISOString(),
  workflow: '6-monitoring-alerts',
  overallStatus: statusReport.overallStatus,
  systemHealth: statusReport.systemHealth.systemStatus,
  totalCost: statusReport.costMonitoring.totalCost,
  budgetUtilization: statusReport.costMonitoring.budgetUtilization,
  queueHealth: statusReport.queueStatus.overall.health,
  totalPending: statusReport.queueStatus.overall.totalPending,
  totalFailed: statusReport.queueStatus.overall.totalFailed,
  imageSuccessRate: statusReport.imageValidation.successRate,
  criticalIssues: statusReport.imageValidation.criticalIssues,
  totalAlerts: statusReport.alerts.totalAlerts,
  criticalAlerts: statusReport.alerts.criticalAlerts,
  warningAlerts: statusReport.alerts.warningAlerts
};

// In production, this would write to a proper logging system
console.log('Monitoring Results:', JSON.stringify(logEntry, null, 2));

return [{ json: logEntry }];
