// Extract and validate submissions array from webhook payload
const input = $input.first().json;

// Handle both direct submissions and webhook body structure
const submissions = input.body?.submissions || input.submissions || [];
const orderData = input.body?.orderData || input.orderData || {};

console.log('=== WORKFLOW 2B STARTED ===');
console.log('Input structure:', Object.keys(input));
console.log(`Processing ${submissions.length} submissions`);
console.log(`Order: ${orderData.amazonOrderId || 'UNKNOWN'}`);
console.log('============================');

// Validate that we have submissions
if (submissions.length === 0) {
  console.error('No submissions found!');
  console.error('Checked paths: input.body.submissions and input.submissions');
  console.error('Available input keys:', Object.keys(input));
  if (input.body) {
    console.error('Body keys:', Object.keys(input.body));
  }
  throw new Error('No submissions received from Workflow 2A');
}

// Validate orderData
if (!orderData.amazonOrderId || !orderData.characterHash) {
  console.warn('âš ï¸ orderData missing or incomplete - may cause issues in Workflow 3');
  console.warn('Available orderData fields:', Object.keys(orderData));
}

// Check first submission has required fields
const firstSubmission = submissions[0];
console.log('First submission fields:', Object.keys(firstSubmission));

// Required fields for polling
const requiredFields = ['requestId', 'statusUrl'];
const missingFields = requiredFields.filter(field => !firstSubmission[field]);

if (missingFields.length > 0) {
  console.error('âŒ Missing required fields:', missingFields);
  console.error('Available fields:', Object.keys(firstSubmission));
  throw new Error(`Submissions missing required fields: ${missingFields.join(', ')}`);
}

console.log('âœ“ All submissions have required fields');
console.log('âœ“ orderData validated');

// Return each submission as a separate item with orderData preserved
// Also normalize field names (poseNumber vs currentPoseNumber)
return submissions.map(s => ({ 
  json: {
    ...s,
    currentPoseNumber: s.currentPoseNumber || s.poseNumber, // Handle both field names
    orderData: orderData,
    retryCount: 0,
    maxRetries: 5
  }
}));

// Poll status for each submission
const item = $input.first().json;
const statusUrl = item.statusUrl;
const requestId = item.requestId;
const poseNumber = item.currentPoseNumber;

console.log(`[${poseNumber}] Checking status for request: ${requestId}`);

return [{
  json: {
    ...item,
    needsStatusCheck: true
  }
}];

// Parse Bria status response
const item = $input.first().json;
const originalData = item;
const briaResponse = item;

const status = briaResponse.status || 'UNKNOWN';
const poseNumber = originalData.currentPoseNumber;

console.log(`[${poseNumber}] Status: ${status}`);

// Determine next action based on status
let shouldDownload = false;
let shouldRetry = false;
let shouldNotifyHuman = false;
let resultUrl = null;

if (status === 'COMPLETED' || status === 'SUCCESS') {
  shouldDownload = true;
  resultUrl = briaResponse.result_url || briaResponse.url;
  console.log(`[${poseNumber}] âœ“ Ready for download`);
} else if (status === 'IN_PROGRESS' || status === 'PROCESSING') {
  shouldRetry = originalData.retryCount < originalData.maxRetries;
  if (shouldRetry) {
    console.log(`[${poseNumber}] â³ Still processing, will retry (${originalData.retryCount + 1}/${originalData.maxRetries})`);
  } else {
    console.log(`[${poseNumber}] âŒ Max retries exceeded - NEEDS HUMAN INTERVENTION`);
    shouldNotifyHuman = true;
  }
} else if (status === 'ERROR' || status === 'FAILED') {
  console.log(`[${poseNumber}] âŒ Processing failed - NEEDS HUMAN INTERVENTION`);
  shouldNotifyHuman = true;
} else {
  console.log(`[${poseNumber}] âš ï¸ Unknown status: ${status} - NEEDS HUMAN INTERVENTION`);
  shouldNotifyHuman = true;
}

return [{
  json: {
    ...originalData,
    briaStatus: status,
    resultUrl: resultUrl,
    shouldDownload: shouldDownload,
    shouldRetry: shouldRetry,
    shouldNotifyHuman: shouldNotifyHuman,
    statusCheckedAt: new Date().toISOString()
  }
}];

// Store downloaded image and prepare for upload
const item = $input.first();
const poseNumber = item.json.currentPoseNumber;
const orderData = item.json.orderData;

// Get binary data
const binaryData = item.binary?.data;

if (!binaryData) {
  throw new Error(`No binary data received for pose ${poseNumber}`);
}

console.log(`[${poseNumber}] âœ“ Downloaded image (${binaryData.fileSize || 'unknown'} bytes)`);

// Prepare for R2 upload with consistent naming
const fileName = `character_pose_${poseNumber}_processed.png`;
const characterHash = orderData?.characterHash || 'unknown';
const r2Path = `characters/${characterHash}/${fileName}`;

return [{
  json: {
    ...item.json,
    fileName: fileName,
    r2Path: r2Path,
    processedWithBria: true,
    processingError: false,
    downloadedAt: new Date().toISOString()
  },
  binary: {
    data: binaryData
  }
}];

// Clean up binary after upload
const item = $input.first().json;
const poseNumber = item.currentPoseNumber;

console.log(`[${poseNumber}] âœ“ Uploaded to R2: ${item.r2Path}`);

return [{
  json: {
    ...item,
    uploadComplete: true,
    fileUrl: `https://pub-92cec53654f84771956bc84dfea65baa.r2.dev/${item.r2Path}`
  }
}];

// Create error notification for human intervention
const item = $input.first().json;
const poseNumber = item.currentPoseNumber;
const orderData = item.orderData;

console.log(`[${poseNumber}] ðŸš¨ CREATING ERROR NOTIFICATION FOR HUMAN`);

const errorDetails = {
  errorType: 'BRIA_PROCESSING_FAILED',
  poseNumber: poseNumber,
  orderId: orderData?.amazonOrderId || 'UNKNOWN',
  characterHash: orderData?.characterHash || 'UNKNOWN',
  briaRequestId: item.requestId,
  briaStatusUrl: item.statusUrl,
  briaStatus: item.briaStatus,
  retryCount: item.retryCount,
  maxRetries: item.maxRetries,
  timestamp: new Date().toISOString(),

  // Human-readable message
  message: `Background removal failed for pose ${poseNumber} in order ${orderData?.amazonOrderId}. Status: ${item.briaStatus}. Retries attempted: ${item.retryCount}/${item.maxRetries}.`,

  // Action required
  actionRequired: 'Manual background removal or image regeneration needed',

  // Links for quick access
  briaStatusLink: item.statusUrl,
  orderLink: `https://your-admin-panel.com/orders/${orderData?.amazonOrderId}`
};

return [{
  json: {
    ...item,
    fileName: null,
    r2Path: null,
    processedWithBria: false,
    processingError: true,
    errorDetails: errorDetails,
    needsHumanIntervention: true
  }
}];

// Mark error as notified
const item = $input.first().json;
const poseNumber = item.currentPoseNumber;

console.log(`[${poseNumber}] âœ“ Human notified of error`);

return [{
  json: {
    ...item,
    humanNotified: true,
    notifiedAt: new Date().toISOString()
  }
}];

// Calculate exponential backoff delay
const item = $input.first().json;
const retryCount = item.retryCount || 0;

// Exponential backoff: 30s, 60s, 120s, 240s, 480s
const baseDelay = 30;
const delay = baseDelay * Math.pow(2, retryCount);

console.log(`Retry ${retryCount + 1}: Waiting ${delay} seconds...`);

return [{
  json: {
    ...item,
    retryCount: retryCount + 1,
    delaySeconds: delay
  }
}];

// Max retries exceeded - send to error handling
const item = $input.first().json;
const poseNumber = item.currentPoseNumber;

console.log(`[${poseNumber}] âŒ Max retries (${item.maxRetries}) exceeded`);

return [{
  json: {
    ...item,
    shouldNotifyHuman: true,
    briaStatus: 'MAX_RETRIES_EXCEEDED'
  }
}];

// Generate final summary with error handling
const items = $input.all();
const orderData = items[0]?.json?.orderData || {};

console.log('\n=== WORKFLOW 2B COMPLETE ===');
console.log(`Total items processed: ${items.length}`);

const processedImages = items.map(item => ({
  poseNumber: item.json.currentPoseNumber,
  fileName: item.json.fileName,
  r2Path: item.json.r2Path,
  fileUrl: item.json.fileUrl,
  briaProcessed: item.json.processedWithBria || false,
  processingError: item.json.processingError || false,
  needsHumanIntervention: item.json.needsHumanIntervention || false,
  briaStatus: item.json.briaStatus
}));

const stats = {
  totalProcessed: items.length,
  successfulBria: processedImages.filter(i => i.briaProcessed).length,
  errors: processedImages.filter(i => i.processingError).length,
  needingHumanIntervention: processedImages.filter(i => i.needsHumanIntervention).length
};

console.log(`âœ“ Bria processed: ${stats.successfulBria}`);
console.log(`âŒ Errors: ${stats.errors}`);
console.log(`ðŸš¨ Need human intervention: ${stats.needingHumanIntervention}`);
console.log('===========================\n');

// Determine if workflow can proceed to Workflow 3
const canProceed = stats.errors === 0;

if (!canProceed) {
  console.error('âš ï¸ CANNOT PROCEED TO WORKFLOW 3 - Errors require human intervention');
}

return [{
  json: {
    orderData: orderData,
    processedImages: processedImages,
    ...stats,
    completedAt: new Date().toISOString(),
    workflow2BComplete: true,
    canProceedToWorkflow3: canProceed,
    errors: canProceed ? null : processedImages.filter(i => i.processingError)
  }
}];

// Cannot proceed - log and notify
const summary = $input.first().json;

console.log('ðŸ›‘ WORKFLOW STOPPED - Human intervention required');
console.log(`Order: ${summary.orderData?.amazonOrderId}`);
console.log(`Errors: ${summary.errors?.length || 0}`);

return [{
  json: {
    ...summary,
    workflow3Triggered: false,
    workflowStopped: true,
    stopReason: 'Errors require human intervention before proceeding'
  }
}];
