// Extract and validate submissions array from webhook payload
const input = $input.first().json;
const submissions = input.submissions || [];
const orderData = input.orderData || {};

console.log('=== WORKFLOW 2B STARTED ===');
console.log(`Processing ${submissions.length} submissions`);
console.log(`Order: ${orderData.amazonOrderId || 'UNKNOWN'}`);
console.log('============================');

// Validate that we have submissions
if (submissions.length === 0) {
  throw new Error('No submissions received from Workflow 2A');
}

// Validate orderData
if (!orderData.amazonOrderId || !orderData.characterHash) {
  console.warn('⚠️ orderData missing or incomplete - may cause issues in Workflow 3');
  console.warn('Available orderData fields:', Object.keys(orderData));
}

// Check first submission has required fields
const firstSubmission = submissions[0];
const requiredFields = ['requestId', 'statusUrl', 'extractedImageData', 'currentPoseNumber'];
const missingFields = requiredFields.filter(field => !firstSubmission[field]);

if (missingFields.length > 0) {
  console.error('❌ Missing required fields:', missingFields);
  console.error('Available fields:', Object.keys(firstSubmission));
  throw new Error(`Submissions missing required fields: ${missingFields.join(', ')}`);
}

console.log('✓ All submissions have required fields');
console.log('✓ orderData validated');

// Return each submission as a separate item with orderData preserved
return submissions.map(s => ({ 
  json: {
    ...s,
    orderData: orderData,
    retryCount: 0,
    maxRetries: 5
  }
}));

// Poll status for each submission
const item = $input.first().json;
const statusUrl = item.statusUrl;
const requestId = item.requestId;
const poseNumber = item.currentPoseNumber;

console.log(`[${poseNumber}] Checking status for request: ${requestId}`);

return [{
  json: {
    ...item,
    needsStatusCheck: true
  }
}];

// Parse Bria status response
const item = $input.first().json;
const originalData = item;
const briaResponse = item;

const status = briaResponse.status || 'UNKNOWN';
const poseNumber = originalData.currentPoseNumber;

console.log(`[${poseNumber}] Status: ${status}`);

// Determine next action based on status
let shouldDownload = false;
let shouldRetry = false;
let shouldUseFallback = false;
let resultUrl = null;

if (status === 'COMPLETED' || status === 'SUCCESS') {
  shouldDownload = true;
  resultUrl = briaResponse.result_url || briaResponse.url;
  console.log(`[${poseNumber}] ✓ Ready for download`);
} else if (status === 'IN_PROGRESS' || status === 'PROCESSING') {
  shouldRetry = originalData.retryCount < originalData.maxRetries;
  if (shouldRetry) {
    console.log(`[${poseNumber}] ⏳ Still processing, will retry (${originalData.retryCount + 1}/${originalData.maxRetries})`);
  } else {
    console.log(`[${poseNumber}] ❌ Max retries exceeded, using fallback`);
    shouldUseFallback = true;
  }
} else if (status === 'ERROR' || status === 'FAILED') {
  console.log(`[${poseNumber}] ❌ Processing failed, using fallback`);
  shouldUseFallback = true;
} else {
  console.log(`[${poseNumber}] ⚠️ Unknown status: ${status}, using fallback`);
  shouldUseFallback = true;
}

return [{
  json: {
    ...originalData,
    briaStatus: status,
    resultUrl: resultUrl,
    shouldDownload: shouldDownload,
    shouldRetry: shouldRetry,
    shouldUseFallback: shouldUseFallback,
    statusCheckedAt: new Date().toISOString()
  }
}];

// Store downloaded image and prepare for upload
const item = $input.first();
const poseNumber = item.json.currentPoseNumber;
const orderData = item.json.orderData;

// Get binary data
const binaryData = item.binary?.data;

if (!binaryData) {
  throw new Error(`No binary data received for pose ${poseNumber}`);
}

console.log(`[${poseNumber}] ✓ Downloaded image (${binaryData.fileSize || 'unknown'} bytes)`);

// Prepare for R2 upload with consistent naming
const fileName = `character_pose_${poseNumber}_processed.png`;
const characterHash = orderData?.characterHash || 'unknown';
const r2Path = `characters/${characterHash}/${fileName}`;

return [{
  json: {
    ...item.json,
    fileName: fileName,
    r2Path: r2Path,
    processedWithBria: true,
    fallbackUsed: false,
    downloadedAt: new Date().toISOString()
  },
  binary: {
    data: binaryData
  }
}];

// Clean up binary after upload
const item = $input.first().json;
const poseNumber = item.currentPoseNumber;

console.log(`[${poseNumber}] ✓ Uploaded to R2: ${item.r2Path}`);

return [{
  json: {
    ...item,
    uploadComplete: true,
    fileUrl: `https://r2.yourdomain.com/${item.r2Path}`
  }
}];

// Use original image as fallback
const item = $input.first().json;
const poseNumber = item.currentPoseNumber;
const orderData = item.orderData;

console.log(`[${poseNumber}] Using fallback image (original with background)`);

// Decode original image from base64
const imageData = item.extractedImageData;
if (!imageData || imageData.length < 100) {
  throw new Error(`No fallback image data available for pose ${poseNumber}`);
}

// Remove data URL prefix if present
const base64Data = imageData.replace(/^data:image\/\w+;base64,/, '');
const buffer = Buffer.from(base64Data, 'base64');

// Prepare for R2 upload
const fileName = `character_pose_${poseNumber}_processed.png`;
const characterHash = orderData?.characterHash || 'unknown';
const r2Path = `characters/${characterHash}/${fileName}`;

return [{
  json: {
    ...item,
    fileName: fileName,
    r2Path: r2Path,
    processedWithBria: false,
    fallbackUsed: true,
    fallbackReason: item.briaStatus || 'UNKNOWN'
  },
  binary: {
    data: {
      data: buffer.toString('base64'),
      mimeType: 'image/png',
      fileName: fileName,
      fileExtension: 'png'
    }
  }
}];

// Clean up fallback binary after upload
const item = $input.first().json;
const poseNumber = item.currentPoseNumber;

console.log(`[${poseNumber}] ✓ Uploaded fallback to R2: ${item.r2Path}`);

return [{
  json: {
    ...item,
    uploadComplete: true,
    fileUrl: `https://r2.yourdomain.com/${item.r2Path}`
  }
}];

// Calculate exponential backoff delay
const item = $input.first().json;
const retryCount = item.retryCount || 0;

// Exponential backoff: 30s, 60s, 120s, 240s, 480s
const baseDelay = 30;
const delay = baseDelay * Math.pow(2, retryCount);

console.log(`Retry ${retryCount + 1}: Waiting ${delay} seconds...`);

return [{
  json: {
    ...item,
    retryCount: retryCount + 1,
    delaySeconds: delay
  }
}];

// Max retries exceeded - mark for fallback
const item = $input.first().json;
const poseNumber = item.currentPoseNumber;

console.log(`[${poseNumber}] ❌ Max retries (${item.maxRetries}) exceeded`);

return [{
  json: {
    ...item,
    shouldUseFallback: true,
    briaStatus: 'MAX_RETRIES_EXCEEDED'
  }
}];

// Generate final summary with orderData preserved
const items = $input.all();
const orderData = items[0]?.json?.orderData || {};

console.log('\n=== WORKFLOW 2B COMPLETE ===');
console.log(`Total items processed: ${items.length}`);

const processedImages = items.map(item => ({
  poseNumber: item.json.currentPoseNumber,
  fileName: item.json.fileName,
  r2Path: item.json.r2Path,
  fileUrl: item.json.fileUrl,
  briaProcessed: item.json.processedWithBria || false,
  fallbackUsed: item.json.fallbackUsed || false,
  briaStatus: item.json.briaStatus
}));

const stats = {
  totalProcessed: items.length,
  successfulBria: processedImages.filter(i => i.briaProcessed).length,
  fallbacksUsed: processedImages.filter(i => i.fallbackUsed).length
};

console.log(`✓ Bria processed: ${stats.successfulBria}`);
console.log(`⚠️ Fallbacks used: ${stats.fallbacksUsed}`);
console.log('===========================\n');

return [{
  json: {
    orderData: orderData,
    processedImages: processedImages,
    ...stats,
    completedAt: new Date().toISOString(),
    workflow2BComplete: true
  }
}];
