const orderData = $input.first().json;

const mockOrderItems = {
  payload: {
    OrderItems: [
      {
        ASIN: 'B0LITTLEHERO001',
        SellerSKU: 'LITTLE_HERO_BOOK_CUSTOM',
        Title: `Little Hero Book - Emma's Adventure - Child Name: Emma - Skin Tone: medium - Hair Color: brown - Hair Style: short/curly - Age: 5 - Pronouns: she/her - Favorite Color: purple - Animal Guide: unicorn - Clothing Style: dress`,
        QuantityOrdered: 1,
        ItemPrice: { Amount: '29.99', CurrencyCode: 'USD' }
      }
    ]
  }
};

console.log(`Generated mock order items for order: ${orderData.amazonOrderId}`);
return [{ json: mockOrderItems }];

const orderItems = $input.first().json.payload.OrderItems || [];
const orderData = $('Parse Orders').first().json;

const customProduct = orderItems.find(item => 
  item.ASIN === 'B0LITTLEHERO001' || 
  item.SellerSKU?.includes('LITTLE_HERO_BOOK')
);

if (!customProduct) {
  console.log('No custom product found in order items');
  return [];
}

const productTitle = customProduct.Title || '';
const customizationData = {
  childName: extractFromTitle(productTitle, 'child_name') || 'Adventure Hero',
  skinTone: extractFromTitle(productTitle, 'skin_tone') || 'medium',
  hairColor: extractFromTitle(productTitle, 'hair_color') || 'brown',
  hairStyle: extractFromTitle(productTitle, 'hair_style') || 'short/straight',
  age: extractFromTitle(productTitle, 'age') || '5',
  pronouns: extractFromTitle(productTitle, 'pronouns') || 'they/them',
  favoriteColor: extractFromTitle(productTitle, 'favorite_color') || 'blue',
  animalGuide: extractFromTitle(productTitle, 'animal_guide') || 'dog',
  clothingStyle: extractFromTitle(productTitle, 'clothing_style') || 't-shirt and shorts'
};

const characterHash = generateCharacterHash(customizationData);

const completeOrderData = {
  ...orderData,
  characterSpecs: customizationData,
  characterHash: characterHash,
  productInfo: {
    asin: customProduct.ASIN,
    sku: customProduct.SellerSKU,
    title: customProduct.Title,
    quantity: customProduct.QuantityOrdered
  },
  status: 'pending_validation',
  createdAt: new Date().toISOString()
};

console.log(`Extracted character specs for ${customizationData.childName}:`, customizationData);
return [{ json: completeOrderData }];

function extractFromTitle(title, key) {
  const patterns = {
    child_name: /child[\s_-]?name[\s_-]?([a-zA-Z]+)/i,
    skin_tone: /skin[\s_-]?tone[\s_-]?([a-zA-Z]+)/i,
    hair_color: /hair[\s_-]?color[\s_-]?([a-zA-Z]+)/i,
    hair_style: /hair[\s_-]?style[\s_-]?([a-zA-Z]+)/i,
    age: /age[\s_-]?([0-9]+)/i,
    pronouns: /pronouns[\s_-]?([a-zA-Z/]+)/i,
    favorite_color: /favorite[\s_-]?color[\s_-]?([a-zA-Z]+)/i,
    animal_guide: /animal[\s_-]?guide[\s_-]?([a-zA-Z]+)/i,
    clothing_style: /clothing[\s_-]?style[\s_-]?([a-zA-Z\s]+)/i
  };

  const match = title.match(patterns[key]);
  return match ? match[1] : null;
}

function generateCharacterHash(specs) {
  const hashString = `${specs.skinTone}-${specs.hairColor}-${specs.hairStyle}-${specs.age}-${specs.pronouns}`;
  let hash = 0;
  for (let i = 0; i < hashString.length; i++) {
    const char = hashString.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return Math.abs(hash).toString(16).substring(0, 8);
}

const orderData = $input.first().json;

const validationErrors = [];

if (!orderData.amazonOrderId) {
  validationErrors.push('Missing Amazon Order ID');
}

if (!orderData.customerEmail || orderData.customerEmail === 'N/A') {
  validationErrors.push('Missing customer email');
}

if (!orderData.shippingAddress) {
  validationErrors.push('Missing shipping address');
}

const charSpecs = orderData.characterSpecs;
if (!charSpecs) {
  validationErrors.push('Missing character specifications');
} else {
  if (!charSpecs.childName || charSpecs.childName.length < 2) {
    validationErrors.push('Invalid child name');
  }

  const validSkinTones = ['light', 'medium', 'dark', 'tan', 'olive'];
  if (!validSkinTones.includes(charSpecs.skinTone)) {
    validationErrors.push('Invalid skin tone');
  }

  const validHairColors = ['black', 'brown', 'blonde', 'red', 'auburn'];
  if (!validHairColors.includes(charSpecs.hairColor)) {
    validationErrors.push('Invalid hair color');
  }

  const validHairStyles = ['short/straight', 'short/curly', 'medium/straight', 'medium/curly', 'pony-tail', 'pom-pom', 'afro'];
  if (!validHairStyles.includes(charSpecs.hairStyle)) {
    validationErrors.push('Invalid hair style');
  }

  const age = parseInt(charSpecs.age);
  if (isNaN(age) || age < 3 || age > 7) {
    validationErrors.push('Invalid age (must be 3-7)');
  }

  const validColors = ['red', 'blue', 'green', 'yellow', 'purple', 'orange', 'pink', 'brown', 'black', 'white'];
  if (!validColors.includes(charSpecs.favoriteColor)) {
    validationErrors.push('Invalid favorite color');
  }

  const validAnimalGuides = ['dog', 'cat', 't-rex', 'unicorn', 'tiger', 'lion', 'owl'];
  if (!validAnimalGuides.includes(charSpecs.animalGuide)) {
    validationErrors.push('Invalid animal guide');
  }

  const validClothingStyles = ['t-shirt and shorts', 'dress'];
  if (!validClothingStyles.includes(charSpecs.clothingStyle)) {
    validationErrors.push('Invalid clothing style');
  }
}

if (validationErrors.length === 0) {
  orderData.status = 'validated';
  orderData.validationErrors = [];
  console.log(`‚úÖ Order validation passed for ${orderData.amazonOrderId}`);
} else {
  orderData.status = 'validation_failed';
  orderData.validationErrors = validationErrors;
  console.log(`‚ùå Order validation failed for ${orderData.amazonOrderId}:`, validationErrors);
}

orderData.validatedAt = new Date().toISOString();

return [{ json: orderData }];

