    'Content-Type': 'application/json'
  },
  qs: {
    select: '*'
  },
  json: true
});

// Calculate cost statistics
const now = Date.now();
const last24h = now - (24 * 60 * 60 * 1000);
const last7d = now - (7 * 24 * 60 * 60 * 1000);

const costStats = {
  timestamp: new Date().toISOString(),
  period: 'current',
  last24h: { ai: 0, print: 0, total: 0, orders: 0 },
  last7d: { ai: 0, print: 0, total: 0, orders: 0 },
  allTime: { ai: 0, print: 0, total: 0, orders: orders.length }
};

