  const orderTime = new Date(order.created_at).getTime();
  const aiCost = parseFloat(order.ai_generation_cost || 0);
  const printCost = parseFloat(order.print_cost || 0);
  const totalCost = parseFloat(order.total_cost || 0);

  // All time
  costStats.allTime.ai += aiCost;
  costStats.allTime.print += printCost;
  costStats.allTime.total += totalCost;

  // Last 7 days
  if (orderTime >= last7d) {
    costStats.last7d.ai += aiCost;
    costStats.last7d.print += printCost;
    costStats.last7d.total += totalCost;
    costStats.last7d.orders++;
  }

  // Last 24 hours
  if (orderTime >= last24h) {
    costStats.last24h.ai += aiCost;
    costStats.last24h.print += printCost;
    costStats.last24h.total += totalCost;
    costStats.last24h.orders++;
  }
});

// Budget tracking
