  daily: {
    limit: dailyBudget,
    used: costStats.last24h.total,
    remaining: dailyBudget - costStats.last24h.total,
    utilization: (costStats.last24h.total / dailyBudget) * 100,
    exceeded: costStats.last24h.total > dailyBudget
