const orders = await this.helpers.request({
  method: 'GET',
  url: `${supabaseUrl}/rest/v1/orders`,
  headers: {
    'apikey': supabaseKey,
    'Authorization': `Bearer ${supabaseKey}`,
    'Content-Type': 'application/json'
  },
  qs: {
    select: '*'
  },
  json: true
});

// Categorize orders by workflow stage
const queueStatus = {
  timestamp: new Date().toISOString(),
  queues: {
    orderIntake: { pending: 0, processing: 0, completed: 0, failed: 0, avgTime: 0 },
    aiGeneration: { pending: 0, processing: 0, completed: 0, failed: 0, avgTime: 0 },
    bookAssembly: { pending: 0, processing: 0, completed: 0, failed: 0, avgTime: 0 },
    humanReview: { pending: 0, processing: 0, completed: 0, failed: 0, avgTime: 0 },
    printFulfillment: { pending: 0, processing: 0, completed: 0, failed: 0, avgTime: 0 }
  },
  overall: {
    totalOrders: orders.length,
    totalPending: 0,
    totalProcessing: 0,
    totalCompleted: 0,
    totalFailed: 0
  }
};

// Categorize each order
orders.forEach(order => {
  const status = order.status || 'unknown';
  const workflow = order.workflow_step || 'unknown';

  // Order Intake
  if (status === 'pending_validation') {
    queueStatus.queues.orderIntake.pending++;
  } else if (status === 'queued_for_processing') {
    queueStatus.queues.orderIntake.completed++;
  } else if (status === 'validation_failed') {
    queueStatus.queues.orderIntake.failed++;
  }

  // AI Generation
  if (status === 'ai_generation_in_progress') {
    queueStatus.queues.aiGeneration.processing++;
  } else if (status === 'ai_generation_completed') {
    queueStatus.queues.aiGeneration.completed++;
  } else if (status === 'ai_generation_failed' || status === 'ai_generation_required') {
    queueStatus.queues.aiGeneration.failed++;
  }

  // Book Assembly
  if (status === 'book_assembly_in_progress') {
    queueStatus.queues.bookAssembly.processing++;
  } else if (status === 'book_assembly_completed' && !order.human_approved) {
    queueStatus.queues.bookAssembly.completed++;
  } else if (status === 'book_assembly_failed') {
    queueStatus.queues.bookAssembly.failed++;
  }

  // Human Review
  if (status === 'book_assembly_completed' && order.human_approved === null) {
    queueStatus.queues.humanReview.pending++;
  } else if (status === 'book_assembly_completed' && order.human_approved === true) {
    queueStatus.queues.humanReview.completed++;
  } else if (status === 'book_assembly_completed' && order.human_approved === false) {
    queueStatus.queues.humanReview.failed++;
  }

  // Print Fulfillment
  if (status === 'print_submission_in_progress') {
    queueStatus.queues.printFulfillment.processing++;
  } else if (status === 'fulfillment_completed') {
    queueStatus.queues.printFulfillment.completed++;
  } else if (status === 'print_failed' || status === 'fulfillment_failed') {
    queueStatus.queues.printFulfillment.failed++;
  }
});

// Calculate overall totals
Object.values(queueStatus.queues).forEach(queue => {
  queueStatus.overall.totalPending += queue.pending;
  queueStatus.overall.totalProcessing += queue.processing;
  queueStatus.overall.totalCompleted += queue.completed;
  queueStatus.overall.totalFailed += queue.failed;
});

