    attempt2: 0,
    attempt3Plus: 0
  },
  alerts: []
};

let totalQaScore = 0;

orders.forEach(order => {
  // QA Score tracking
  if (order.qa_score !== null && order.qa_score !== undefined) {
    qualityMetrics.withQaScore++;
    totalQaScore += parseFloat(order.qa_score);

    const score = parseFloat(order.qa_score);
    if (score >= 0.9) qualityMetrics.qualityDistribution.excellent++;
    else if (score >= 0.8) qualityMetrics.qualityDistribution.good++;
    else if (score >= 0.7) qualityMetrics.qualityDistribution.fair++;
    else qualityMetrics.qualityDistribution.poor++;
  }

  // Human review tracking
  if (order.requires_human_review) {
    qualityMetrics.humanReview.required++;

    if (order.human_approved === null) {
      qualityMetrics.humanReview.pending++;
    } else if (order.human_approved === true) {
      qualityMetrics.humanReview.approved++;
    } else if (order.human_approved === false) {
      qualityMetrics.humanReview.rejected++;
    }
  }

  // Regeneration tracking
  if (order.regeneration_attempt > 0) {
    qualityMetrics.regenerations.total++;

    if (order.regeneration_attempt === 1) qualityMetrics.regenerations.attempt1++;
    else if (order.regeneration_attempt === 2) qualityMetrics.regenerations.attempt2++;
    else qualityMetrics.regenerations.attempt3Plus++;
  }
});

// Calculate average QA score
if (qualityMetrics.withQaScore > 0) {
  qualityMetrics.averageQaScore = totalQaScore / qualityMetrics.withQaScore;
