// Check character consistency across all poses
const orderData = $input.first().json;

const characterConsistency = {
  timestamp: new Date().toISOString(),
  orderId: orderData.amazon_order_id || orderData.amazonOrderId,
  characterSpecs: orderData.character_specs || orderData.characterSpecs,
  poses: [],
  consistencyScore: 0,
  issues: [],
  passed: false
};

// Simulate character consistency analysis for each of 12 poses
for (let i = 1; i <= 12; i++) {
  const poseAnalysis = {
    poseNumber: i,
    imagePath: `/assets/generated/${orderData.amazon_order_id}/pose${i}.png`,
    hasImage: Math.random() > 0.05,
    correctSkinTone: Math.random() > 0.1,
    correctHairColor: Math.random() > 0.1,
    correctHairStyle: Math.random() > 0.15,
    correctClothing: Math.random() > 0.2,
    correctPose: Math.random() > 0.1,
    imageQuality: Math.random() * 0.3 + 0.7,
    consistencyScore: 0,
    issues: []
  };

  // Calculate consistency score
  const checks = [
    poseAnalysis.hasImage,
    poseAnalysis.correctSkinTone,
    poseAnalysis.correctHairColor,
    poseAnalysis.correctHairStyle,
    poseAnalysis.correctClothing,
    poseAnalysis.correctPose
  ];
  poseAnalysis.consistencyScore = (checks.filter(Boolean).length / checks.length) * 100;

  // Identify issues
  if (!poseAnalysis.hasImage) poseAnalysis.issues.push('Missing image');
  if (!poseAnalysis.correctSkinTone) poseAnalysis.issues.push('Incorrect skin tone');
  if (!poseAnalysis.correctHairColor) poseAnalysis.issues.push('Incorrect hair color');
  if (!poseAnalysis.correctHairStyle) poseAnalysis.issues.push('Incorrect hair style');
  if (!poseAnalysis.correctClothing) poseAnalysis.issues.push('Incorrect clothing');
  if (!poseAnalysis.correctPose) poseAnalysis.issues.push('Incorrect pose');
  if (poseAnalysis.imageQuality < 0.8) poseAnalysis.issues.push('Low image quality');

  characterConsistency.poses.push(poseAnalysis);
}

// Calculate overall consistency score
const totalScore = characterConsistency.poses.reduce((sum, pose) => sum + pose.consistencyScore, 0);
characterConsistency.consistencyScore = totalScore / characterConsistency.poses.length;
characterConsistency.passed = characterConsistency.consistencyScore >= 80;

// Collect all issues
characterConsistency.issues = characterConsistency.poses.flatMap(pose => 
  pose.issues.map(issue => ({
    poseNumber: pose.poseNumber,
    issue: issue,
    severity: pose.consistencyScore < 60 ? 'high' : pose.consistencyScore < 80 ? 'medium' : 'low'
  }))
);

console.log(`‚úÖ Character consistency: ${characterConsistency.consistencyScore.toFixed(1)}% (${characterConsistency.passed ? 'PASS' : 'FAIL'})`);
console.log(`   Issues found: ${characterConsistency.issues.length}`);

return [{ json: { ...orderData, characterConsistency } }];

// Validate image quality for all generated images
const orderData = $input.first().json;

const imageQuality = {
  timestamp: new Date().toISOString(),
  orderId: orderData.amazon_order_id || orderData.amazonOrderId,
  images: [],
  overallQualityScore: 0,
  issues: [],
  passed: false
};

// Check base character + 12 poses
const imageTypes = ['base_character', 'pose1', 'pose2', 'pose3', 'pose4', 'pose5', 'pose6', 'pose7', 'pose8', 'pose9', 'pose10', 'pose11', 'pose12'];

for (const imageType of imageTypes) {
  const imageAnalysis = {
    imageType: imageType,
    imagePath: `/assets/generated/${orderData.amazon_order_id}/${imageType}.png`,
    hasImage: Math.random() > 0.02,
    correctDimensions: Math.random() > 0.05,
    hasTransparency: Math.random() > 0.1,
    fileSize: Math.random() * 500000 + 100000,
    resolution: Math.random() * 200 + 800,
    colorAccuracy: Math.random() * 0.3 + 0.7,
    sharpness: Math.random() * 0.3 + 0.7,
    compression: Math.random() * 0.2 + 0.8,
    qualityScore: 0,
    issues: []
  };

  // Calculate quality score
  const qualityChecks = [
    imageAnalysis.hasImage,
    imageAnalysis.correctDimensions,
    imageAnalysis.hasTransparency,
    imageAnalysis.fileSize > 50000,
    imageAnalysis.resolution >= 800,
    imageAnalysis.colorAccuracy >= 0.8,
    imageAnalysis.sharpness >= 0.8,
    imageAnalysis.compression >= 0.9
  ];
  imageAnalysis.qualityScore = (qualityChecks.filter(Boolean).length / qualityChecks.length) * 100;

  // Identify issues
  if (!imageAnalysis.hasImage) imageAnalysis.issues.push('Missing image');
  if (!imageAnalysis.correctDimensions) imageAnalysis.issues.push('Incorrect dimensions');
  if (!imageAnalysis.hasTransparency) imageAnalysis.issues.push('Missing transparency');
  if (imageAnalysis.fileSize < 50000) imageAnalysis.issues.push('File too small');
  if (imageAnalysis.resolution < 800) imageAnalysis.issues.push('Low resolution');
  if (imageAnalysis.colorAccuracy < 0.8) imageAnalysis.issues.push('Poor color accuracy');
  if (imageAnalysis.sharpness < 0.8) imageAnalysis.issues.push('Low sharpness');
  if (imageAnalysis.compression < 0.9) imageAnalysis.issues.push('Poor compression');

  imageQuality.images.push(imageAnalysis);
}

// Calculate overall quality score
const totalScore = imageQuality.images.reduce((sum, img) => sum + img.qualityScore, 0);
imageQuality.overallQualityScore = totalScore / imageQuality.images.length;
imageQuality.passed = imageQuality.overallQualityScore >= 85;

// Collect all issues
imageQuality.issues = imageQuality.images.flatMap(img => 
  img.issues.map(issue => ({
    imageType: img.imageType,
    issue: issue,
    severity: img.qualityScore < 70 ? 'high' : img.qualityScore < 85 ? 'medium' : 'low'
  }))
);

console.log(`‚úÖ Image quality: ${imageQuality.overallQualityScore.toFixed(1)}% (${imageQuality.passed ? 'PASS' : 'FAIL'})`);
console.log(`   Issues found: ${imageQuality.issues.length}`);

return [{ json: { ...orderData, imageQuality } }];

// Test PDF generation quality and completeness
const orderData = $input.first().json;

const pdfQuality = {
  timestamp: new Date().toISOString(),
  orderId: orderData.amazon_order_id || orderData.amazonOrderId,
  pdfPath: orderData.final_book_url || `/assets/generated/${orderData.amazon_order_id}/final_book.pdf`,
  pages: [],
  overallScore: 0,
  issues: [],
  passed: false
};

// Check all 14 pages (front cover + 12 interior + back cover)
for (let i = 1; i <= 14; i++) {
  const pageAnalysis = {
    pageNumber: i,
    hasPage: Math.random() > 0.01,
    correctDimensions: Math.random() > 0.02,
    hasText: i > 1 && i < 14 ? Math.random() > 0.05 : true,
    hasCharacter: i > 1 && i < 14 ? Math.random() > 0.03 : true,
    hasBackground: Math.random() > 0.02,
    hasAnimal: i > 1 && i < 14 ? Math.random() > 0.05 : true,
    textReadable: Math.random() > 0.1,
    colorsCorrect: Math.random() > 0.08,
    layoutCorrect: Math.random() > 0.05,
    printReady: Math.random() > 0.02,
    pageScore: 0,
    issues: []
  };

  // Calculate page score
  const pageChecks = [
    pageAnalysis.hasPage,
    pageAnalysis.correctDimensions,
    pageAnalysis.hasText,
    pageAnalysis.hasCharacter,
    pageAnalysis.hasBackground,
    pageAnalysis.hasAnimal,
    pageAnalysis.textReadable,
    pageAnalysis.colorsCorrect,
    pageAnalysis.layoutCorrect,
    pageAnalysis.printReady
  ];
  pageAnalysis.pageScore = (pageChecks.filter(Boolean).length / pageChecks.length) * 100;

  // Identify issues
  if (!pageAnalysis.hasPage) pageAnalysis.issues.push('Missing page');
  if (!pageAnalysis.correctDimensions) pageAnalysis.issues.push('Incorrect dimensions');
  if (!pageAnalysis.hasText) pageAnalysis.issues.push('Missing text');
  if (!pageAnalysis.hasCharacter) pageAnalysis.issues.push('Missing character');
  if (!pageAnalysis.hasBackground) pageAnalysis.issues.push('Missing background');
  if (!pageAnalysis.hasAnimal) pageAnalysis.issues.push('Missing animal');
  if (!pageAnalysis.textReadable) pageAnalysis.issues.push('Text not readable');
  if (!pageAnalysis.colorsCorrect) pageAnalysis.issues.push('Incorrect colors');
  if (!pageAnalysis.layoutCorrect) pageAnalysis.issues.push('Incorrect layout');
  if (!pageAnalysis.printReady) pageAnalysis.issues.push('Not print ready');

  pdfQuality.pages.push(pageAnalysis);
}

// Calculate overall PDF score
const totalScore = pdfQuality.pages.reduce((sum, page) => sum + page.pageScore, 0);
pdfQuality.overallScore = totalScore / pdfQuality.pages.length;
pdfQuality.passed = pdfQuality.overallScore >= 90;

// Collect all issues
pdfQuality.issues = pdfQuality.pages.flatMap(page => 
  page.issues.map(issue => ({
    pageNumber: page.pageNumber,
    issue: issue,
    severity: page.pageScore < 80 ? 'high' : page.pageScore < 90 ? 'medium' : 'low'
  }))
);

console.log(`‚úÖ PDF quality: ${pdfQuality.overallScore.toFixed(1)}% (${pdfQuality.passed ? 'PASS' : 'FAIL'})`);
console.log(`   Issues found: ${pdfQuality.issues.length}`);

return [{ json: { ...orderData, pdfQuality } }];

// Verify print specifications (8x10, 300 DPI, etc.)
const orderData = $input.first().json;

const printSpecs = {
  timestamp: new Date().toISOString(),
  orderId: orderData.amazon_order_id || orderData.amazonOrderId,
  specifications: {
    pageSize: '8x10 inches',
    pageCount: 14,
    dpi: 300,
    colorMode: 'CMYK',
    bleed: '0.125 inches',
    trim: '0.25 inches',
    binding: 'Perfect bound',
    paperType: '80lb matte',
    coverType: 'Softcover'
  },
  compliance: {},
  issues: [],
  passed: false
};

// Simulate compliance checking
printSpecs.compliance = {
  correctPageSize: Math.random() > 0.02,
  correctPageCount: Math.random() > 0.01,
  correctDPI: Math.random() > 0.03,
  correctColorMode: Math.random() > 0.05,
  correctBleed: Math.random() > 0.08,
  correctTrim: Math.random() > 0.05,
  correctBinding: Math.random() > 0.02,
  correctPaperType: Math.random() > 0.03,
  correctCoverType: Math.random() > 0.02,
  fileSize: Math.random() * 50000000 + 10000000,
  fileFormat: 'PDF',
  compression: Math.random() * 0.2 + 0.8
};

printSpecs.compliance.fileSizeCompliant = printSpecs.compliance.fileSize <= 100000000;
printSpecs.compliance.compressionCompliant = printSpecs.compliance.compression >= 0.9;

// Calculate compliance score
const complianceChecks = [
  printSpecs.compliance.correctPageSize,
  printSpecs.compliance.correctPageCount,
  printSpecs.compliance.correctDPI,
  printSpecs.compliance.correctColorMode,
  printSpecs.compliance.correctBleed,
  printSpecs.compliance.correctTrim,
  printSpecs.compliance.correctBinding,
  printSpecs.compliance.correctPaperType,
  printSpecs.compliance.correctCoverType,
  printSpecs.compliance.fileSizeCompliant,
  printSpecs.compliance.compressionCompliant
];

const complianceScore = (complianceChecks.filter(Boolean).length / complianceChecks.length) * 100;
printSpecs.complianceScore = complianceScore;
printSpecs.passed = complianceScore >= 95;

// Identify issues
if (!printSpecs.compliance.correctPageSize) printSpecs.issues.push('Incorrect page size');
if (!printSpecs.compliance.correctPageCount) printSpecs.issues.push('Incorrect page count');
if (!printSpecs.compliance.correctDPI) printSpecs.issues.push('Incorrect DPI');
if (!printSpecs.compliance.correctColorMode) printSpecs.issues.push('Incorrect color mode');
if (!printSpecs.compliance.correctBleed) printSpecs.issues.push('Incorrect bleed');
if (!printSpecs.compliance.correctTrim) printSpecs.issues.push('Incorrect trim');
if (!printSpecs.compliance.correctBinding) printSpecs.issues.push('Incorrect binding');
if (!printSpecs.compliance.correctPaperType) printSpecs.issues.push('Incorrect paper type');
if (!printSpecs.compliance.correctCoverType) printSpecs.issues.push('Incorrect cover type');
if (!printSpecs.compliance.fileSizeCompliant) printSpecs.issues.push('File too large');
if (!printSpecs.compliance.compressionCompliant) printSpecs.issues.push('Poor compression');

console.log(`‚úÖ Print specs: ${complianceScore.toFixed(1)}% (${printSpecs.passed ? 'PASS' : 'FAIL'})`);
console.log(`   Issues found: ${printSpecs.issues.length}`);

return [{ json: { ...orderData, printSpecs } }];

// Flag quality issues and determine overall status
const orderData = $input.first().json;

const qualityFlags = {
  timestamp: new Date().toISOString(),
  orderId: orderData.amazon_order_id || orderData.amazonOrderId,
  overallStatus: 'unknown',
  criticalIssues: [],
  warningIssues: [],
  infoIssues: [],
  totalIssues: 0,
  qualityScore: 0,
  requiresManualReview: false,
  canProceed: false
};

// Collect all issues
const allIssues = [];

if (orderData.characterConsistency) {
  allIssues.push(...orderData.characterConsistency.issues.map(issue => ({
    ...issue,
    category: 'character_consistency',
    check: 'Character Consistency'
  })));
}

if (orderData.imageQuality) {
  allIssues.push(...orderData.imageQuality.issues.map(issue => ({
    ...issue,
    category: 'image_quality',
    check: 'Image Quality'
  })));
}

if (orderData.pdfQuality) {
  allIssues.push(...orderData.pdfQuality.issues.map(issue => ({
    ...issue,
    category: 'pdf_quality',
    check: 'PDF Quality'
  })));
}

if (orderData.printSpecs) {
  allIssues.push(...orderData.printSpecs.issues.map(issue => ({
    issue: issue,
    severity: 'high',
    category: 'print_specs',
    check: 'Print Specifications'
  })));
}

// Categorize by severity
qualityFlags.criticalIssues = allIssues.filter(issue => issue.severity === 'high');
qualityFlags.warningIssues = allIssues.filter(issue => issue.severity === 'medium');
qualityFlags.infoIssues = allIssues.filter(issue => issue.severity === 'low');
qualityFlags.totalIssues = allIssues.length;

// Calculate overall quality score
const totalChecks = 4;
const passedChecks = [
  orderData.characterConsistency?.passed || false,
  orderData.imageQuality?.passed || false,
  orderData.pdfQuality?.passed || false,
  orderData.printSpecs?.passed || false
].filter(Boolean).length;

qualityFlags.qualityScore = (passedChecks / totalChecks) * 100;

// Determine status
if (qualityFlags.criticalIssues.length > 0) {
  qualityFlags.overallStatus = 'critical';
  qualityFlags.requiresManualReview = true;
  qualityFlags.canProceed = false;
} else if (qualityFlags.warningIssues.length > 5 || qualityFlags.qualityScore < 80) {
  qualityFlags.overallStatus = 'warning';
  qualityFlags.requiresManualReview = true;
  qualityFlags.canProceed = false;
} else if (qualityFlags.qualityScore >= 90) {
  qualityFlags.overallStatus = 'excellent';
  qualityFlags.requiresManualReview = false;
  qualityFlags.canProceed = true;
} else if (qualityFlags.qualityScore >= 80) {
  qualityFlags.overallStatus = 'good';
  qualityFlags.requiresManualReview = false;
  qualityFlags.canProceed = true;
} else {
  qualityFlags.overallStatus = 'poor';
  qualityFlags.requiresManualReview = true;
  qualityFlags.canProceed = false;
}

console.log(`üèÅ Quality status: ${qualityFlags.overallStatus}`);
console.log(`   Overall score: ${qualityFlags.qualityScore.toFixed(1)}%`);
console.log(`   Total issues: ${qualityFlags.totalIssues} (${qualityFlags.criticalIssues.length} critical, ${qualityFlags.warningIssues.length} warning)`);
console.log(`   Can proceed: ${qualityFlags.canProceed}`);

return [{ json: { ...orderData, qualityFlags } }];

// Generate comprehensive quality report
const orderData = $input.first().json;

const qualityReport = {
  timestamp: new Date().toISOString(),
  orderId: orderData.amazon_order_id || orderData.amazonOrderId,
  customerName: orderData.character_specs?.childName || orderData.characterSpecs?.childName || 'Unknown',
  overallStatus: orderData.qualityFlags.overallStatus,
  qualityScore: orderData.qualityFlags.qualityScore,
  canProceed: orderData.qualityFlags.canProceed,
  requiresManualReview: orderData.qualityFlags.requiresManualReview,
  checks: {},
  issues: {
    critical: orderData.qualityFlags.criticalIssues,
    warning: orderData.qualityFlags.warningIssues,
    info: orderData.qualityFlags.infoIssues,
    total: orderData.qualityFlags.totalIssues
  },
  recommendations: [],
  nextSteps: []
};

// Add check results
if (orderData.characterConsistency) {
  qualityReport.checks.characterConsistency = {
    passed: orderData.characterConsistency.passed,
    score: orderData.characterConsistency.consistencyScore,
    issues: orderData.characterConsistency.issues.length
  };
}

if (orderData.imageQuality) {
  qualityReport.checks.imageQuality = {
    passed: orderData.imageQuality.passed,
    score: orderData.imageQuality.overallQualityScore,
    issues: orderData.imageQuality.issues.length
  };
}

if (orderData.pdfQuality) {
  qualityReport.checks.pdfQuality = {
    passed: orderData.pdfQuality.passed,
    score: orderData.pdfQuality.overallScore,
    issues: orderData.pdfQuality.issues.length
  };
}

if (orderData.printSpecs) {
  qualityReport.checks.printSpecs = {
    passed: orderData.printSpecs.passed,
    score: orderData.printSpecs.complianceScore,
    issues: orderData.printSpecs.issues.length
  };
}

// Generate recommendations
if (orderData.qualityFlags.criticalIssues.length > 0) {
  qualityReport.recommendations.push({
    priority: 'critical',
    action: 'Fix critical issues before proceeding',
    description: 'Address all critical quality issues to ensure customer satisfaction'
  });
}

if (orderData.qualityFlags.warningIssues.length > 0) {
  qualityReport.recommendations.push({
    priority: 'high',
    action: 'Review warning issues',
    description: 'Consider addressing warning issues to improve quality'
  });
}

if (orderData.qualityFlags.qualityScore < 90) {
  qualityReport.recommendations.push({
    priority: 'medium',
    action: 'Improve overall quality',
    description: 'Focus on improving quality scores across all checks'
  });
}

// Generate next steps
if (qualityReport.requiresManualReview) {
  qualityReport.nextSteps.push({
    step: 'Manual Review Required',
    description: 'Order requires manual review before proceeding',
    assignedTo: 'Quality Assurance Team',
    priority: 'high'
  });
} else if (qualityReport.canProceed) {
  qualityReport.nextSteps.push({
    step: 'Proceed to Fulfillment',
    description: 'Order meets quality standards and can proceed to fulfillment',
    assignedTo: 'Fulfillment Team',
    priority: 'normal'
  });
} else {
  qualityReport.nextSteps.push({
    step: 'Fix Issues',
    description: 'Address quality issues before proceeding',
    assignedTo: 'Development Team',
    priority: 'high'
  });
}

console.log(`üìä Quality report generated`);
console.log(`   Status: ${qualityReport.overallStatus}`);
console.log(`   Score: ${qualityReport.qualityScore.toFixed(1)}%`);
console.log(`   Recommendations: ${qualityReport.recommendations.length}`);
console.log(`   Next steps: ${qualityReport.nextSteps.length}`);

return [{ json: qualityReport }];

// Mock: Send quality alert email (if critical or warning)
const qualityReport = $input.first().json;

const emailAlert = {
  timestamp: new Date().toISOString(),
  emailSent: false,
  shouldSendEmail: ['critical', 'warning'].includes(qualityReport.overallStatus),
  recipients: ['qa@littleherobooks.com', 'admin@littleherobooks.com'],
  subject: `${qualityReport.overallStatus === 'critical' ? 'üö®' : '‚ö†Ô∏è'} Quality Alert - Order ${qualityReport.orderId}`,
  emailBody: {
    orderId: qualityReport.orderId,
    customerName: qualityReport.customerName,
    overallStatus: qualityReport.overallStatus,
    qualityScore: qualityReport.qualityScore,
    criticalIssues: qualityReport.issues.critical.length,
    warningIssues: qualityReport.issues.warning.length,
    recommendations: qualityReport.recommendations,
    nextSteps: qualityReport.nextSteps
  },
  mock: true
};

if (emailAlert.shouldSendEmail) {
  console.log(`üìß Would send email alert to QA team`);
  console.log(`   Subject: ${emailAlert.subject}`);
  console.log(`   Critical issues: ${emailAlert.emailBody.criticalIssues}`);
  console.log(`   Warning issues: ${emailAlert.emailBody.warningIssues}`);
} else {
  console.log(`‚úÖ No email alert needed (status: ${qualityReport.overallStatus})`);
}

return [{ json: { ...qualityReport, emailAlert } }];

// Log quality results
const qualityReport = $input.first().json;

const logEntry = {
  timestamp: new Date().toISOString(),
  workflow: '7-quality-assurance',
  orderId: qualityReport.orderId,
  customerName: qualityReport.customerName,
  overallStatus: qualityReport.overallStatus,
  qualityScore: qualityReport.qualityScore,
  canProceed: qualityReport.canProceed,
  requiresManualReview: qualityReport.requiresManualReview,
  totalIssues: qualityReport.issues.total,
  criticalIssues: qualityReport.issues.critical.length,
  warningIssues: qualityReport.issues.warning.length,
  infoIssues: qualityReport.issues.info.length,
  checks: qualityReport.checks,
  recommendations: qualityReport.recommendations.length,
  nextSteps: qualityReport.nextSteps.length,
  emailAlertSent: qualityReport.emailAlert?.shouldSendEmail || false
};

console.log(`‚úÖ Quality Assurance Complete`);
console.log(`   Order: ${logEntry.orderId}`);
console.log(`   Status: ${logEntry.overallStatus}`);
console.log(`   Score: ${logEntry.qualityScore.toFixed(1)}%`);
console.log(`   Can proceed: ${logEntry.canProceed}`);

return [{ json: logEntry }];
